    incrementInt = () => {
        this.setState({
            qtdInteira: ++this.state.qtdInteira,
            qtdTotal: ++this.state.qtdTotal,
            valorTotal: parseInt(this.state.valorTotal) + parseInt(this.state.event.valorIngressoInt)
        })

    }

    decrementInt = () => {
        if (this.state.qtdInteira !== 0) {
            this.setState({
                qtdInteira: --this.state.qtdInteira,
                qtdTotal: --this.state.qtdTotal,
                valorTotal: parseInt(this.state.valorTotal) - parseInt(this.state.event.valorIngressoInt)
            })
        }
    }

    incrementMeia = () => {
        this.setState({
